var items = [ "node.js", "meteor", "browserside" ]
var current = 0
var speed = 3000

$(function () {
	var animateFrameworks
	setInterval(animateFrameworks = function () {
		$(".frameworks").animate({ opacity: 0 }, 1000, function (){
			$(this)
				.text(items[current++ % items.length])
				.animate({opacity: 1}, 1000)
		})
	}, speed)
	animateFrameworks()
});
